contracts/utils/DataStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

abstract contract DataStorage {

    mapping(uint256 => Katibeh) idToToken;
    mapping(uint256 => KatibehData) idToTokenData;
    mapping(bytes32 => uint256) URIsRegistered;
    mapping(uint256 => bytes) _dappData;
    mapping(uint256 => bytes) _signatures;

    struct Payee {
        address addr;
        uint16 share;
    }

    struct Katibeh {
        address creator;
        uint256 signTime;
        uint256 initTime;
        uint256 expTime;
        string tokenURI;
        bytes data;
        uint256[] toTokenId;
        bytes32[] tags;
        Payee[] owners;
    }

    struct KatibehData {
        uint256 expTime;
        bytes data;
        Payee[] owners;
    }

    event NewToken(
        uint256 indexed tokenId, 
        address indexed creator, 
        bytes indexed data, 
        uint256 signTime,
        uint256 initTime,
        uint256 expTime
    );

    event NewReply(
        uint256 indexed tokenId,
        uint256 indexed toTokenId
    );

    event Tags(
        uint256 tokenId,
        bytes32 indexed tag1, 
        bytes32 indexed tag2, 
        bytes32 indexed tag3
    );

    function _registerURI(string calldata _uri, uint256 tokenId) internal {
        bytes32 uriHash = keccak256(abi.encodePacked(_uri));
        require(getUriId(uriHash) == 0, "DataStorage: uri registered already");
        URIsRegistered[uriHash] = tokenId;
    }

    function _setCollectData(
        uint256 tokenId,
        Katibeh calldata katibeh
    ) internal {
        idToTokenData[tokenId] = KatibehData(katibeh.expTime, katibeh.data, katibeh.owners);
        emit NewToken(tokenId, katibeh.creator, katibeh.data, katibeh.signTime, katibeh.initTime, katibeh.expTime);
        uint256 toIdLen = katibeh.toTokenId.length;
        for (uint256 i; i < toIdLen; i++){
            emit NewReply(tokenId, katibeh.toTokenId[i]);
        }
    }

    function _burnData(uint256 tokenId) internal {
        Katibeh storage katibeh = idToToken[tokenId];
        delete _signatures[tokenId];
        katibeh.tokenURI = "data:application/json;base64,eyJuYW1lIjogIlRoaXMgdG9rZW4gaXMgYnVybmVkLiIsImRlc2NyaXB0aW9uIjogIlRva2VuIGlzIG5vdCBjb2xsZWN0aWJsZSBvbiB0aGlzIG5ldHdvcmsuIn0";
    }

    function _setMintData(
        uint256 tokenId,
        Katibeh calldata katibeh
    ) internal {
        idToToken[tokenId] = katibeh;
        emit NewToken(tokenId, katibeh.creator, katibeh.data, katibeh.signTime, katibeh.initTime, katibeh.expTime);
        uint256 toIdLen = katibeh.toTokenId.length;
        for (uint256 i; i < toIdLen; i++){
            emit NewReply(tokenId, katibeh.toTokenId[i]);
        }
    }

    function _setDappData(uint256 tokenId, bytes calldata data) internal {
        _dappData[tokenId] = data;
    }

    function _setSignature(uint256 tokenId, bytes calldata sig) internal {
        _signatures[tokenId] = sig;
    }

    function _tokenURI(uint256 tokenId) internal view returns(string memory) {
        return idToToken[tokenId].tokenURI;
    }

    function _emitTags(uint256 tokenId, bytes32[] calldata tags) internal {
        uint256 len = tags.length;
        require(len <= 3, "DataStorage: tags length must be 3");
        bytes32 empty;
        emit Tags(
            tokenId, 
            len > 0 ? tags[0] : empty,
            len > 1 ? tags[1] : empty, 
            len > 2 ? tags[2] : empty
        );
    }

    function getUriId(bytes32 uriHash) public view returns(uint256) {
        return URIsRegistered[uriHash];
    }

    function tokensInfoBatch(uint256[] calldata tokenId) public view returns(
        Katibeh[] memory katibeh, bytes[] memory sig, bytes[] memory dappData
    ) {
        uint256 len = tokenId.length;
        katibeh = new Katibeh[](len);
        sig = new bytes[](len);
        dappData = new bytes[](len);

        for(uint256 i; i < len; i++) {
            (katibeh[i], sig[i], dappData[i]) = tokenInfo(tokenId[i]);
        }
    }

    function tokenInfo(uint256 tokenId) public view returns(
        Katibeh memory katibeh, bytes memory sig, bytes memory dappData
    ) {
        katibeh = idToToken[tokenId];
        if(block.timestamp >= katibeh.initTime) {
            sig = _signatures[tokenId];
        }
        dappData = _dappData[tokenId];
    }
}

contracts/katibeh721/Katibeh721.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "../utils/qHash.sol";
import "../utils/VerifySig.sol";
import "../utils/DataStorage.sol";
import "./utils/TraceStorage.sol";

contract Katibeh721 is ERC721, ERC721Enumerable, ERC721Burnable, DataStorage, TraceStorage {
    using Strings for uint256;
    using qHash for bytes;
    using VerifySig for bytes;

    constructor() ERC721("Katibeh721", "KATIBEH") {}

    function timeStamp() public view returns(uint256) {
        return block.timestamp;
    }

    function getHash(Katibeh calldata katibeh) public view returns(bytes32) {
        require(
            katibeh.signTime < block.timestamp + 1 hours &&
            katibeh.signTime > block.timestamp - 1 hours,
            "Katibeh721: more than one hour mint time difference."
        );
        require(
            katibeh.signTime <= katibeh.initTime &&
            katibeh.initTime <= katibeh.expTime,
            "Katibeh721: mint time must be less than init time & init time must be less than expire time."
        );
        return keccak256(abi.encode(katibeh));
    }

    function safeMint(
        Katibeh calldata katibeh,
        bytes calldata sig,
        bytes calldata dappData
    ) public returns(uint256 tokenId) {
        tokenId = sig.q();
        require(
            sig.verify(
                katibeh.creator,
                getHash(katibeh)
            ),
            "Katibeh721: Invalid signature"
        );
        _safeMint(katibeh.creator, tokenId);
        _registerURI(katibeh.tokenURI, tokenId);
        _setMintData(tokenId, katibeh);
        _emitTags(tokenId, katibeh.tags);
        _setDappData(tokenId, dappData);
        _setSignature(tokenId, sig);
    }


    function safeMintAndSetTraceStorage( 
        Katibeh calldata katibeh,
        bytes calldata sig,
        bytes calldata dappData
    ) public {
        uint256 tokenId = safeMint(katibeh, sig, dappData);

        _setTokenTraceable(tokenId);
        _setCreatorToken(katibeh.creator, tokenId);
        _addCreator(katibeh.creator);
        uint256 toIdLen = katibeh.toTokenId.length;
        for (uint256 i; i < toIdLen; i++){
            _setTokenReply(tokenId, katibeh.toTokenId[i]);
            _setCreatorReply(idToToken[katibeh.toTokenId[i]].creator, tokenId);
        }
        _registerTags(tokenId, katibeh.tags);
    }

    function _burn(uint256 tokenId) internal override {
        super._burn(tokenId);
        _burnData(tokenId);
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory)
    {
        return _tokenURI(tokenId);
    }

    // The following functions are overrides required by Solidity.

    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal
        override(ERC721, ERC721Enumerable)
    {
        require(
            from == address(0) || to == address(0),
            "tokens cannot be transfered on testnet"
        );
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

